  if (shadow.a <= u_shadowThreshold) {
      o.a = 0.0;
      ALPHA_TEST(o);
      return o;
  }
  float stp = 1.0 / float(shadowBlur);
  float mul = stp;
  for (int i = 1; i < shadowBlur; i++) {
    vec2 shadowOffset = vec2(u_shadowOffset.x - float(i), u_shadowOffset.y - float(i));
    vec2 shadowUv = shadowOffset / size;
    vec4 shadow = texture(cc_spriteTexture, v_uv + vec2(1.0, u_globalDirection) * shadowUv);
    if (shadow.a > u_shadowThreshold) {
      mul = mul + stp;
